import SwiftUI
import RealityKit
import Foundation //base64変換に使うもの

struct ContentView : View {
    var body: some View {
        ARViewContainer().edgesIgnoringSafeArea(.all)
    }
}

struct ARViewContainer: UIViewRepresentable {
    
    func makeUIView(context: Context) -> ARView {
        
        let arView = ARView(frame: .zero)
        
        //Create a cube modelf
        //let mesh = MeshResource.generateBox(size: 0.1, cornerRadius: 0.005)
        //let material = SimpleMaterial(color: .blue, roughness: 0.15, isMetallic: true)
        //let model = ModelEntity(mesh: mesh, materials: [material])
        
        //画像ファイル定義シンプルテスト
        //let image:String
        //image = "hitokage.png"
        
        //PNG画像読み込み
        
        
        
        let box = ModelEntity(mesh: .generateBox(size: simd_make_float3(0.5, 0.5, 0.2)))
        
        let base64:String = ""
        // テクスチャ・リソースとして画像を読み込む
        if let texture = try? TextureResource.load(named: base64) {
            
            var imageMaterial = UnlitMaterial()
            // アンリット・マテリアルのテクスチャに設定する
            imageMaterial.baseColor = MaterialColorParameter.texture(texture)
            // ボックスのマテリアルに設定する
            box.model?.materials = [imageMaterial]
        }







//Create horizontal plane anchor for the content
let anchor = AnchorEntity(.plane(.horizontal, classification: .any, minimumBounds: SIMD2<Float>(0.2, 0.2)))
anchor.children.append(box)//model->box変更した

//Add the horizontal plane anchor to the scene
arView.scene.anchors.append(anchor)


return arView

}

func updateUIView(_ uiView: ARView, context: Context) {}

}

